function steamroller(arr) {
  var newArr = arr.reduce(function(a, b) {
    return Array.isArray(a) ? a.concat(b) : [a].concat(b);
  });
  return isFlat(arr) ? newArr : steamroller(newArr); 
}

function isFlat(arr) {
  return arr.reduce(function(a, b) {
    return (a === true || !Array.isArray(a)) && !Array.isArray(b);
  });
}

function translate(sentence) {
  return sentence.replace(/[A-Za-z]+/gi, function(w) {
    var cap = /[A-Z]/.test(w);
    var word = /[aeiou]/i.test(w[0]) ? w + 'way' : w.replace(/([^aeiou]+)(\w+)/i, '$2$1' + 'ay');
    return cap ? word[0].toUpperCase() + word.slice(1).toLowerCase() : word.toLowerCase();
  });
};

import re


def abbreviate(s):
    key = re.sub(r'(\w)(\w*)(\w)', r'\1_\2_\3', s).split('_')
    for part in range(1, len(key), 2):
        if len(key[part]) > 1:
            key[part] = str(len(key[part]))
    return '.join(key)'

function permutations(data) {
  var perms = [];
  if(typeof data === 'string') data = data.slice().split('');
  function _permute(item, mem){
    if(item.length === 0) return perms.push(mem.join(''));
    for(let i=0;i<item.length;i++) {
      let x = item.splice(i,1);
      mem.push(x);
      _permute(item, mem);
      mem.pop();
      item.splice(i,0,x);
    }
  }
  _permute(data, []);
  return perms.filter((p, i) => perms.indexOf(p) === i);
}

var isPerfectPower = function(n){
  let factors = [];
  for(let m=2; m*m<=n; m++) {
    if(n % m == 0) factors.push(m);
  }
  for(let i=2; i<=Math.sqrt(n) + 1; i++) {
    let result = factors.filter((num) => Math.pow(num, i) === n);
    if(result.length) return result.concat(i);
  }
  return null;
}

function convert(num) {
  var romanKeys = [['M', 1000], ['CM', 900], ['D', 500], ['CD', 400], ['C', 100], ['XC', 90], ['L', 50], ['XL', 40], ['X', 10], ['IX', 9], ['V', 5], ['IV', 4], ['I', 1]];

  return romanKeys.map(function(key) {
    var letter = key[0];
    var count = Math.floor(num / key[1]);
    var roman = Array(count).fill(letter).join('');
    num -= key[1] * count;

    return roman;
  }).join('');
}

function factorial(num) {
  return Array(num + 1).join(0).split('').map((n, i) => i + 1).reduce((a, b) => a * b);
}

function permCount(str) {
  var dupes = str.slice().split('').sort().reduce((a, b) => a[a.length - 1] === b ? a + b : a + ' ' + b).split(' ');
  var dupFactor = dupes.map((l) => factorial(l.length)).reduce((a, b) => a * b);
  return factorial(str.length)/dupFactor;
}

function mutation(arr) {
  var str = arr[0].toLowerCase();
  return arr[1].toLowerCase().split('').reduce(function(a, b) {
    return (a === true || str.indexOf(a) >= 0) && str.indexOf(b) >= 0;
  });
}

function chunk(arr, size) {
  return arr.filter(function(c, i) {
    return i % size === 0;
  }).map(function(c, i) {
    return arr.slice(i * size, (i * size) + size);
  });
}

function repeat(str, num) {
  return num < 0 ? '' : Array(num + 1).join(str);
}

function largestOfFour(arr) {
  return arr.map(function(ar) {
    return ar.reduce(function(a, b) {
      return a > b ? a : b;
    });
  });
}

function palindrome(str) {
  var s = str.toLowerCase().replace(/[^a-z0-9]/g,'');
  return s === s.split('').reverse().join('');
}

def palindrome(string):
    lis = re.sub(r'[^a-z0-9]', '', string.lower())
    return lis == lis[::-1]

def is_prime(num):
    if num < 2:
        return False
    for i in range(2, num - 1):
        if num % i == 0:
            return False
    return True

def descending_order(n)
